import ReactDOM from 'react-dom/client';
import {CartesianGrid, Legend, Line, LineChart, Tooltip, XAxis, YAxis} from "recharts";
import React from "react";
import { App } from './Chart';

function formatTimestamp(unixtimestamp) {
    var date = new Date(unixtimestamp * 1000);
    var hours = String('00' + date.getHours())
    var minutes = String('00' + date.getMinutes());
    var seconds = String('00' + date.getSeconds());
    var formattedTime = hours.substring(hours.length - 2) + ':' + minutes.substring(minutes.length - 2) + ':' + seconds.substring(seconds.length - 2);
    return formattedTime;
}

export function plotComparison(messungen1, messungen2, geraetNummer1, geraetNummer2) {
    if(messungen1 === [] || messungen2 === []){
        return
    }
    messungen1 = messungen1.sort((a, b) => a[1] - b[1])
    messungen1 = messungen1.map(messungen => ([messungen[0].toFixed(2), formatTimestamp(messungen[1])]))
    messungen2 = messungen2.sort((a, b) => a[1] - b[1])
    messungen2 = messungen2.map(messungen => ([messungen[0].toFixed(2), formatTimestamp(messungen[1])]))
    ReactDOM.createRoot(document.getElementById("graphvergleich")).render(
        <div>
        <LineChart
            width={1400}
            height={400}
            data={messungen1}       //TODO: damit die Tooltips funktionieren muss hier und in Zeile 41 was geändert werden
            margin={{
                top: 5,
                right: 30,
                left: 20,
                bottom: 5,
            }}
        >
            <CartesianGrid strokeDasharray="3 3"/>
            <XAxis dataKey="1" /*label={{value: ``, position: "insideBottomLeft", dy: 25}}*/ />
            <YAxis dataKey="0" domain={[0, 8]}/>
            <Tooltip/>
            <Legend/>
            <Line name={'Gerät '+geraetNummer1}
                  //data={messungen1}
                  type="monotone"
                  dataKey="0"
                  stroke="#274796"
                  dot={false}
                  strokeWidth={2}
            />
            <Line name={'Gerät '+geraetNummer2}
                  data={messungen2}
                  type="monotone"
                  dataKey="0"
                  stroke="#F3942F"
                  dot={false}
                  strokeWidth={2}
            />
        </LineChart>


        <App/>
        </div>)
}

export function getChartData(geraetNummer) {

    function makerequest() {
        if (geraetNummer === '-1') {
            console.log("no number supllied")
            return
        }
        axios.get(`/api/get_graph_daten/${geraetNummer}`).then(response => {
            console.log(response)
            var messungen = response.data.messungen;
            messungen = messungen.sort((a, b) => a[1] - b[1])
            messungen = messungen.map(messungen => ([messungen[0].toFixed(2), formatTimestamp(messungen[1])]))

            ReactDOM.createRoot(document.getElementById("graphdaten")).render(
                <LineChart
                    width={1400}
                    height={400}
                    margin={{
                        top: 5,
                        right: 30,
                        left: 20,
                        bottom: 5,
                    }}
                >
                    <CartesianGrid strokeDasharray="3 3"/>
                    <XAxis dataKey="1" /*label={{value: ``, position: "insideBottomLeft", dy: 25}}*/ />
                    <YAxis dataKey="0" domain={[0, 8]}/>
                    <Tooltip/>
                    <Legend/>
                    <Line name={`aktueller Verbrauch von Gerät ${geraetNummer}`}
                          data={messungen}
                          type="monotone"
                          dataKey="0"
                          stroke="#216d41"
                          dot={false}
                          strokeWidth={2}
                    />
                </LineChart>)
        })
    }

    makerequest()
    return (
        <>
            {<div className={'m-5'} id="graphdaten"></div>}
        </>
    )
}
