import * as React from 'react';
// import getPowerConsumption, {getAvrgPowerConsumption, getMaxPowerConsumption} from './PowerConsumption'
import OutlinedInput from '@mui/material/OutlinedInput';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import ListItemText from '@mui/material/ListItemText';
import Select from '@mui/material/Select';
import Checkbox from '@mui/material/Checkbox';
import axios from "axios";
import {plotComparison} from './ChartData';
import getPowerConsumptionValue from './PowerConsumption';

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};

export default function CheckboxesTags() {
    const [selectedIds, setSelectedIds] = React.useState([]);

  const handleChange = (event) => {
    const {
      target: { value },
    } = event;
    console.log(value)
    setSelectedIds(
      // On autofill we get a stringified value.
      typeof value === 'string' ? value.split(' , ') : value,
    );
    const currentValue = typeof value === 'string' ? value.split(',') : value; 
    const respPromises = currentValue.map(item => getData(item));
    console.log(respPromises);
    Promise.all(respPromises).then((values) => {
        plotComparison(values);
        console.log(values);
      });

      const respPromisesConsump = currentValue.map(item => getPowerConsumptionValue(item));
      console.log(respPromisesConsump);
      Promise.all(respPromisesConsump).then((values) => {
        //   написать отображение данных 
          console.log('test1',values);
        });
};
    const options = [];
for (var i = 1; i <= 56; i++) {
    if(i!==50 && i!==30){
    options.push({value: i, text: `Gerät ${i}`});

    }
}
  return ( 
  <div>
    <FormControl sx={{ m: 1, width: 300 }}>
      <InputLabel id="demo-multiple-checkbox-label">Geräte zum Vergleich wählen</InputLabel>
      <Select
        labelId="demo-multiple-checkbox-label"
        id="demo-multiple-checkbox"
        multiple
        value={selectedIds}
        onChange={handleChange}

        input={<OutlinedInput label="Geräte zum Vergleich wählen" />}
        renderValue={(selected) => selected.text.join(' , ')}
        MenuProps={MenuProps}
      >
        {options.map((option) => (
          <MenuItem key={option.text} value={option.value}>
            <Checkbox checked={selectedIds.indexOf(option.value) > -1} />
            <ListItemText primary={option.text} />
          </MenuItem>
        ))}
      </Select>
    </FormControl>
    <div className={'m-5'} id="graphvergleich"></div>
  </div>
);
function getData(geraetNummer) {
    if (geraetNummer !== '-1') {
        return axios.get(`/api/get_graph_daten/${geraetNummer}`).then(response => {
            console.log(response)
            return {id: geraetNummer, data: response.data.messungen};
        })
    }
}


}
