import axios from 'axios';
import ReactDOM from 'react-dom';
export default function getPowerConsumption(geraetNummer) {
    function makerequest() {
    axios.get(`/api/getmqtt_messung/${geraetNummer}`).then(response => {
            console.log(response)
            var messung = Math.round((response.data.messung*100)/100);

            ReactDOM.createRoot(document.getElementById("testblargh")).render(<h1 className="result">{`Verbrauch von Geraet ${geraetNummer}`}: {messung}</h1>)
})
    }
    makerequest()
    return (
        <>
            {<div id="testblargh"></div> }
        </>
    )
}
