import axios from 'axios';
import ReactDOM from 'react-dom/client';
import {CartesianGrid, Legend, Line, LineChart, Tooltip, XAxis, YAxis} from "recharts";
import React from "react";

export default function getChartData(geraetNummer) {
    function makerequest() {
        axios.get(`/api/get_graph_daten/${geraetNummer}`).then(response => {
            console.log(response)
            var messungen = response.data.messungen.map(i=>[Math.round(i[0]),i[1]]);
            console.log(messungen)

            ReactDOM.createRoot(document.getElementById("graphdaten")).render(
                <LineChart
                    width={1000}
                    height={400}
                    data={messungen}
                    margin={{
                        top: 5,
                        right: 30,
                        left: 20,
                        bottom: 5,
                    }}
                >
                    <CartesianGrid strokeDasharray="3 3"/>
                    <XAxis dataKey="1" /*label={{value: ``, position: "insideBottomLeft", dy: 25}}*//>
                    <YAxis dataKey="0" domain={[0, 8]}/>
                    <Tooltip/>
                    <Legend/>
                    <Line name="aktueller Verbrauch" type="monotone" dataKey="0" stroke="#82ca9d"/>
                </LineChart>)
        })
    }

    makerequest()
    return (
        <>
            {<div id="graphdaten"></div>}
        </>
    )
}
