import {useEffect,useState} from 'react';
import * as React from "react";
import axios from "axios";

export default function sequenzList (){
  const options = [];

  const makeSeqRequest = (callback) =>{
    axios.get(`/api/get_sequenzen`).then((response) => {
        callback(response);
      });
    };

    const [sequenzen, setSequenzen] = React.useState([],[]);
    sequenzen.map((sequenz) =>
       options.push({value: sequenz.label, text:sequenz.label}));

//   useEffect(() => {
//     makeSeqRequest((response) => {
//       setSequenzen(response.data);
//     });
//     return () => {};
//   }, []);

  const [selected, setSelected] = React.useState(options[0].value);

  const handleChange = event => {
    console.log(event.target.value);
    setSelected(event.target.value);
  };

  return (
    <div>
      <select value={selected} onChange={handleChange}>
        {options.map(option => (
          <option key={option.value} value={option.value}>
            {option.text}
          </option>
        ))}
      </select>
    </div>
  );
};
