import {useState} from 'react';
import getPowerConsumption, {getMaxPowerConsumption, getAvrgPowerConsumption} from './PowerConsumption'

const App = () => {
    const options = [
        {value: '', text: '--Choose an option--'}
    ];
    for (var i = 1; i <= 56; i++) {
        options.push({value: i, text: `Geraet ${i}`});
    }


    const [selected, setSelected] = useState(options[0].value);

    const handleChange = event => {
        console.log(event.target.value);
        setSelected(event.target.value);

    };

    return (
        <div>
            <select value={selected} onChange={handleChange}>

                {options.map(option => (
                    <option key={option.value} value={option.value}>
                        {option.text}
                    </option>
                ))}
            </select>
            {getPowerConsumption(selected)}
            {getAvrgPowerConsumption(selected)}
            {getMaxPowerConsumption(selected)}
        </div>
    );
};

export default App;
