import axios from 'axios';
import ReactDOM from 'react-dom';

export default function getPowerConsumption(geraetNummer) {
    function makerequest() {
        axios.get(`/api/getmqtt_messung/${geraetNummer}`).then(response => {
            console.log(response)
            var messung = response.data.messung;

            ReactDOM.createRoot(document.getElementById("powerconsumption")).render(<p
                className="result">{`Verbrauch von Geraet ${geraetNummer}`}: {messung.toFixed(2)}</p>,<p>{`Verbrauch von Geraet ${geraetNummer}`}: {messung.toFixed(2)}</p>)
        })
    }

    makerequest()
    return (
        <>
            {<div id="powerconsumption"></div>}
        </>
    )
}

export function getAvrgPowerConsumption(geraetNummer) {
    function makerequest2() {
        axios.get(`/api/get_average/${geraetNummer}`).then(response => {
            console.log(response)
            var durchschnitsverbrauch = response.data.durchschnitsverbrauch;

            ReactDOM.createRoot(document.getElementById("avrgconsumption")).render(<p
                className="result">{`Durchschnittsverbrauch von Geraet ${geraetNummer}`}: {durchschnitsverbrauch.toFixed(2)}</p>)
        })
    }

    makerequest2()
    return (
        <>
            {<div id="avrgconsumption"></div>}
        </>
    )
}

export function getMaxPowerConsumption(geraetNummer) {
    function makerequest3() {
        axios.get(`/api/get_maximum/${geraetNummer}`).then(response => {
            console.log(response)
            let maxverbrauch = response.data.maxverbrauch;

            ReactDOM.createRoot(document.getElementById("maxconsumption")).render(<p
                className="result">{`Maximalverbrauch von Geraet ${geraetNummer}`}: {maxverbrauch.toFixed(2)}</p>)
        })
    }

    makerequest3()
    return (
        <>
            {<div id="maxconsumption"></div>}
        </>
    )
}
