import * as React from 'react';
import TextField from '@mui/material/TextField';
import Autocomplete from '@mui/material/Autocomplete';
import axios from "axios";
import { toast, ToastContainer} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Button from '@mui/material/Button';

const options = ['Option 1', 'Option 2'];

export default function ControllableStates() {
  const [value, setValue] = React.useState(options[0]);
  const [inputValue, setInputValue] = React.useState([[], []]);

   const handleInputChange = event => {
    setInputValue(event.target.inputValue);
  }


  function submitGroup(groupName) {
    axios.get(`/api/gruppe_hinzufuegen/${groupName}'`).catch(function (error) {console.log(error)}).then(response => {
        console.log(response);
        toast('Gruppe gespeichert!')

    })
}
  return (
    <div>
      {/* <div>{`value: ${value !== null ? `'${value}'` : 'null'}`}</div>
      <div>{`inputValue: '${inputValue}'`}</div> */}
      <br />
      <Autocomplete
        value={value}
        onChange={(event, newValue) => {
          setValue(newValue);
        }}
        inputValue={inputValue}
        // onInputChange={handleInputChange}

        id="controllable-states-demo"
        options={options}
        sx={{ width: 300 }}
        renderInput={(params) => <TextField {...params} label="Controllable" />}
      />
       <div id="button3">
                <Button variant="outlined" size="medium" onClick={submitGroup(inputValue)}>  Gruppe speichern </Button>
                </div>
    </div>

  );
}
