import React from "react";
import axios from 'axios';
import ReactDOM from 'react-dom';

export default  function getPowerConsumptionValue(geraetNummer) {

        if (geraetNummer !== '-1') {
           return axios.get(`/api/getmqtt_messung/${geraetNummer}`).then(response => {
                console.log("сука",response)
                return {id: geraetNummer, data: response.data.messung};
            })
        }

}

function getPowerConsumption(geraetNummer, elementId) {
    function makerequest() {
        if (geraetNummer !== '-1') {
            axios.get(`/api/getmqtt_messung/${geraetNummer}`).then(response => {
                console.log(response)
                var messung = response.data.messung;

                ReactDOM.createRoot(document.getElementById(`${elementId}`)).render(<p
                    className="result">{`aktueller Verbrauch von Gerät ${geraetNummer}`}: {messung.toFixed(2)} Wt</p>)
            })
        }
    }


    makerequest()
    return (
        <>
            {<div id={`${elementId}`}></div>}
        </>
    )
}

export function getAvrgPowerConsumption(geraetNummer, elementId) {
    function makerequest2() {
        if (geraetNummer !== '-1') {
            axios.get(`/api/get_average/${geraetNummer}`).then(response => {
                console.log(response)
                var durchschnittsverbrauch = response.data.durchschnitsverbrauch;

                ReactDOM.createRoot(document.getElementById(`${elementId}`)).render(<p
                    className="result">{`durchschnittlicher Verbrauch von Gerät ${geraetNummer}`}: {durchschnittsverbrauch.toFixed(2)}Wt</p>)
            })
        }

    }

    makerequest2()
    return (
        <>
            {<div id={`${elementId}`}></div>}
        </>
    )
}

export function getMaxPowerConsumption(geraetNummer, elementId) {
    function makerequest3() {
        if (geraetNummer !== '-1') {
            axios.get(`/api/get_maximum/${geraetNummer}`).then(response => {
                console.log(response)
                var maxverbrauch = response.data.maxverbrauch;

                ReactDOM.createRoot(document.getElementById(`${elementId}`)).render(<p
                    className="result">{`maximaler Verbrauch von Gerät ${geraetNummer}`}: {maxverbrauch.toFixed(2)} Wt</p>)

            })
        }
    }

    makerequest3()
    return (
        <>
            {<div id={`${elementId}`}></div>}
        </>
    )
}
