import React, {useRef, useState} from 'react';
import getPowerConsumption, {getAvrgPowerConsumption, getMaxPowerConsumption} from './PowerConsumption'
import {getChartData, plotComparison} from "./ChartData";
import axios from "axios";

const App = () => {
    const options = [
        {value: '-1', text: '--Wähle ein Gerät--'}
    ];
    for (var i = 1; i <= 56; i++) {
        options.push({value: i, text: `Gerät ${i}`});
    }

    const [selected, setSelected] = useState(options[0].value);
    const [selectedComparison1, setSelectedComparison1] = useState(options[0].value);
    const [selectedComparison2, setSelectedComparison2] = useState(options[0].value);
    const messungen1 = useRef([]);
    const messungen2 = useRef([]);

    function makerequest1(geraetNummer) {
        if (geraetNummer !== '-1') {
            axios.get(`/api/get_graph_daten/${geraetNummer}`).then(response => {
                console.log(response)
                var messungen = response.data.messungen;
                messungen1.current = messungen;
                plotComparison(messungen1.current, messungen2.current);
            })
        }
    }

    function makerequest2(geraetNummer) {
        if (geraetNummer !== '-1') {
            axios.get(`/api/get_graph_daten/${geraetNummer}`).then(response => {
                console.log(response)
                var messungen = response.data.messungen;
                messungen2.current = messungen;
                plotComparison(messungen1.current, messungen2.current);
            })
        }
    }

    makerequest1(selectedComparison1);

    makerequest2(selectedComparison2);

    const handleChange = event => {
        console.log(event.target.value);
        setSelected(event.target.value);
    };
    const handleChange1 = event => {
        console.log(event.target.value);
        setSelectedComparison1(event.target.value);
    };
    const handleChange2 = event => {
        console.log(event.target.value);
        setSelectedComparison2(event.target.value);
    };

    return (
        <div>
            <div className={'bg-light shadow'}>
            <header className={'fs-1 m-4'}>
                Daten eines Gerätes
            </header>
            <p className={'parameter mt-3 mb-1'}> Gerätenummer auswählen: </p>
            <div className={'w-100 p-2'}>
                <select value={selected} onChange={handleChange}
                        className={'form-control mb-4 mx-auto text-center'} style={{width: '20rem'}}>

                    {options.map(option => (
                        <option key={option.value} value={option.value}>
                            {option.text}
                        </option>
                    ))}
                </select>
            </div>
            {getPowerConsumption(selected, "PowerConsumptionSingleDevice")}
            {getAvrgPowerConsumption(selected, "AvrgPowerConsumptionSingleDevice")}
            {getMaxPowerConsumption(selected, "MaxPowerConsumptionSingleDevice")}
            {getChartData(selected)}
        </div>
            <div className={'bg-light shadow'}>
            <header className={'fs-1 m-4'}>
                Vergleich von Geräten
            </header>
            <div>
                <div className="container">
                    <div className="row">
                        <div className="col-sm">
                            <select value={selectedComparison1} onChange={handleChange1}
                                    className={'form-control w-100 m-4 mx-auto text-center'}>

                                {options.map(option => (
                                    <option key={option.value} value={option.value}>
                                        {option.text}
                                    </option>
                                ))}
                            </select>
                            {getPowerConsumption(selectedComparison1, "PowerConsumptionFirstDevice")}
                            {getAvrgPowerConsumption(selectedComparison1, "AvrgPowerConsumptionFirstDevice")}
                            {getMaxPowerConsumption(selectedComparison1, "MaxPowerConsumptionFirstDevice")}
                        </div>
                        <div className="col-sm">
                            <select value={selectedComparison2} onChange={handleChange2}
                                    className={'form-control w-100 m-4 mx-auto text-center'}>

                                {options.map(option => (
                                    <option key={option.value} value={option.value}>
                                        {option.text}
                                    </option>
                                ))}
                            </select>
                            {getPowerConsumption(selectedComparison2, "PowerConsumptionSecondDevice")}
                            {getAvrgPowerConsumption(selectedComparison2, "AvrgPowerConsumptionSecondDevice")}
                            {getMaxPowerConsumption(selectedComparison2, "MaxPowerConsumptionSecondDevice")}
                        </div>
                    </div>
                </div>


                <div className={'m-5'} id="graphvergleich"></div>
                {//plotComparison(messungen1,messungen2)
                }

            </div>
            </div>
        </div>
    );
};

export default App;
