import ReactDOM from 'react-dom/client';
import React from "react";
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
  } from 'chart.js';
  import { Line } from 'react-chartjs-2';

  ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
  );

  export const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Graphen von ausgewählten Geräten',
      },
    },
  };

function formatTimestamp(unixtimestamp) {
    var date = new Date(unixtimestamp * 1000);
    var hours = String('00' + date.getHours())
    var minutes = String('00' + date.getMinutes());
    var seconds = String('00' + date.getSeconds());
    var formattedTime = hours.substring(hours.length - 2) + ':' + minutes.substring(minutes.length - 2) + ':' + seconds.substring(seconds.length - 2);
    return formattedTime;
}

export function plotComparison(messungen1, messungen2, geraetNummer1, geraetNummer2) {
    if(messungen1 === [] || messungen2 === []){
        return
    }

     messungen1 = messungen1.sort((a, b) => a[1] - b[1])
    // const times = messungen1.map(messungen =>  formatTimestamp(messungen[1]));
    if (messungen1===[]){
        const times = messungen2.map(messungen =>  formatTimestamp(messungen[1]));
    } else{
        const times = messungen1.map(messungen =>  formatTimestamp(messungen[1])); 
    }
    const data1 = messungen1.map(messungen => messungen[0].toFixed(2));
    messungen2 = messungen2.sort((a, b) => a[1] - b[1]);
    const data2 =  messungen2.map(messungen => messungen[0].toFixed(2));
    const data = {
        labels: times,
        datasets: [
          {
            label: `Gerät ${geraetNummer1}`,
            data: data1,
            borderColor: 'rgb(255, 99, 132)',
            backgroundColor: 'rgba(255, 99, 132, 0.5)',
          },
          {
            label: `Gerät ${geraetNummer2}`,
            data: data2,
            borderColor: 'rgb(53, 162, 235)',
            backgroundColor: 'rgba(53, 162, 235, 0.5)',
          },
        ],
      };
    ReactDOM.createRoot(document.getElementById("graphvergleich")).render(
        <Line options={options} data={data} />


    )


    return (
        <>

            {<div className={'m-5'} id="graphdaten"></div>}

        </>
    )
}
